import React from 'react';
import { Checkbox } from '@/components/ui/checkbox';
import { BaseFieldProps } from '@/lib/form-field/form-field';

export const BooleanField: React.FC<BaseFieldProps> = (field) => {
  return <Checkbox onCheckedChange={field.onChange} checked={field.value} {...field} />;
};

import React, { useCallback, useState } from 'react';
import { debounce } from 'lodash-es';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Button, buttonVariants } from '@/components/ui/button';
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from '@/components/ui/command';
import { Check, ChevronsUpDown, Loader2 } from 'lucide-react';
import { cn } from '@/lib/data-table/utils';
import { FormControl } from '@/components/ui/form';
import { CommandLoading } from 'cmdk';
import { BaseFieldProps, Options } from '@/lib/form-field/form-field';
import { useOptionQuery } from '@/hooks/useOptionQuery';
import { createSyntheticInputChange } from '@/lib/form-field/utils';

export const ComboboxField: React.FC<
  BaseFieldProps & {
    options: (searchQuery: string | undefined) => Options;
    displayNoResultDuringSearch?: boolean;
    noResultFallBack?: React.ReactNode;
  }
> = ({
  noResultFallBack = 'No Result found.',
  displayNoResultDuringSearch = true,
  options: optionFn,
  ...field
}) => {
  const [searchQuery, setSearchQuery] = useState<string>();
  const optionsQuery = useOptionQuery(() => optionFn(searchQuery), field.name, {
    q: searchQuery,
  });
  const debounceFN = useCallback(
    debounce((value: string) => {
      setSearchQuery(value);
    }, 500),
    [],
  );

  return (
    <div className={'relative'}>
      <Popover>
        <PopoverTrigger asChild>
          <FormControl className={'w-full'}>
            <Button
              variant='outline'
              role='combobox'
              className={cn('justify-between', !field.value && 'text-muted-foreground')}
            >
              {field.value
                ? optionsQuery.data?.find((opt) => opt.value === field.value)?.label
                : `Select ${field.name}`}
              <ChevronsUpDown className='opacity-50' />
            </Button>
          </FormControl>
        </PopoverTrigger>
        <PopoverContent className='p-0' align='start'>
          <Command>
            <CommandInput
              placeholder={`Search ${field.name}...`}
              className='h-9'
              onValueChange={(value) => {
                debounceFN(value);
              }}
            />
            <CommandList>
              <CommandEmpty
                className={cn(
                  'bg-secondary m-2',
                  buttonVariants({
                    variant: 'ghost',
                    size: 'sm',
                    className: 'w-full',
                  }),
                )}
              >
                {noResultFallBack}
              </CommandEmpty>
              {optionsQuery.isPending && (
                <CommandLoading>
                  <div className={'flex items-center justify-center py-4'}>
                    <Loader2 className={'animate-spin'} />
                  </div>
                </CommandLoading>
              )}
              <CommandGroup>
                {optionsQuery.data?.map((opt) => (
                  <CommandItem
                    value={opt.label?.toString()}
                    key={opt.value}
                    onSelect={() => {
                      field.onChange(createSyntheticInputChange(field.name, opt.value));
                    }}
                  >
                    <span>
                      {opt.icon && <opt.icon />}
                      {opt.label}
                    </span>
                    <span>{opt.count && `(${opt.count})`}</span>
                    <Check
                      className={cn(
                        'ml-auto',
                        opt.value === field.value ? 'opacity-100' : 'opacity-0',
                      )}
                    />
                  </CommandItem>
                ))}
              </CommandGroup>
              {displayNoResultDuringSearch && (
                <CommandItem
                  className={cn(
                    'bg-secondary m-2',
                    buttonVariants({
                      variant: 'ghost',
                      size: 'sm',
                      className: 'w-full',
                    }),
                  )}
                >
                  {noResultFallBack}
                </CommandItem>
              )}
            </CommandList>
          </Command>
        </PopoverContent>
      </Popover>
    </div>
  );
};

import React from 'react';
import { Group, Input, NumberField } from 'react-aria-components';
import { BaseFieldProps } from '@/lib/form-field/form-field';

export const CurrencyField: React.FC<
  BaseFieldProps & {
    formatOptions?: Intl.NumberFormatOptions;
  }
> = ({
  formatOptions = {
    style: 'currency',
    currency: 'INR',
    currencySign: 'accounting',
  },
  ...props
}) => (
  <NumberField {...props} formatOptions={formatOptions}>
    <div className='*:not-first:mt-2'>
      <Group className='border-input doutline-none data-focus-within:border-ring data-focus-within:ring-ring/50 data-focus-within:has-aria-invalid:ring-destructive/20 dark:data-focus-within:has-aria-invalid:ring-destructive/40 data-focus-within:has-aria-invalid:border-destructive relative inline-flex h-9 w-full items-center overflow-hidden rounded-md border text-sm whitespace-nowrap shadow-xs transition-[color,box-shadow] data-disabled:opacity-50 data-focus-within:ring-[3px]'>
        <Input className='bg-input/30 text-foreground flex-1 px-3 py-2 tabular-nums' />
      </Group>
    </div>
  </NumberField>
);

'use client';

import { Input } from '@/components/ui/input';
import React, { useId, useState } from 'react';
import { Calendar } from '@/components/ui/calendar';
import { ChevronDownIcon, ClockIcon } from 'lucide-react';
import { Button } from '@/components/form-field/ui/button';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { format } from 'date-fns';
import { BaseFieldProps } from '@/lib/form-field/form-field';
import { createSyntheticInputChange } from '@/lib/form-field/utils';

export const DateTimeField: React.FC<
  BaseFieldProps & {
    display?: 'date' | 'time' | 'datetime';
  }
> = ({ display = 'datetime', ...field }) => {
  const id = useId();
  const [date, setDate] = useState<Date | undefined>(field.value);
  const shouldDisplayTime = display === 'time' || display === 'datetime';
  const shouldDisplayDate = display === 'date' || display === 'datetime';
  return (
    <div className='flex w-full gap-1'>
      {shouldDisplayDate && (
        <div className='flex flex-1 flex-col gap-3'>
          <Popover>
            <PopoverTrigger asChild>
              <Button
                variant='secondary'
                id='date-picker'
                className='file:text-foreground placeholder:text-muted-foreground selection:bg-primary selection:text-primary-foreground dark:bg-input/30 border-input bg-secondary w-full justify-between border font-normal'
              >
                {date ? format(date, 'PPP') : field.placeholder || 'Select date'}
                <ChevronDownIcon />
              </Button>
            </PopoverTrigger>
            <PopoverContent className='w-auto overflow-hidden p-0' align='start'>
              <Calendar
                mode='single'
                selected={date}
                captionLayout='dropdown'
                onSelect={(date) => {
                  if (!date) return;
                  setDate(date);
                  field.onChange(createSyntheticInputChange(field.name, new Date(date).toString()));
                }}
              />
            </PopoverContent>
          </Popover>
        </div>
      )}
      {shouldDisplayTime && (
        <div className='flex flex-1 flex-col gap-3'>
          <div className='relative grow'>
            <Input
              id={id}
              type='time'
              step='1'
              defaultValue='12:00:00'
              onChange={(e) => {
                const timeValue = e.target.value;
                const [hours, minutes, seconds] = timeValue.split(':').map(Number);
                const newDate = new Date(date || new Date());
                newDate.setHours(hours, minutes, seconds);
                setDate(newDate);
                field.onChange(
                  createSyntheticInputChange(field.name, new Date(newDate).toString()),
                );
              }}
              className='peer appearance-none ps-9 [&::-webkit-calendar-picker-indicator]:hidden [&::-webkit-calendar-picker-indicator]:appearance-none'
            />
            <div className='text-muted-foreground/80 pointer-events-none absolute inset-y-0 start-0 flex items-center justify-center ps-3 peer-disabled:opacity-50'>
              <ClockIcon size={16} aria-hidden='true' />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

import React from 'react';
import { BaseFieldProps } from '@/lib/form-field/form-field';
import ImageDropZone from '@/components/form-field/ui/ImageDropZone';

export const FileField = ({
  display = 'COMPACT',
  ...field
}: BaseFieldProps & {
  multiple?: boolean;
  accept?: string;
  display?: 'COMPACT' | 'FULL';
}) => {
  return (
    <ImageDropZone {...field} accept={field.accept} multiple={field.multiple} display={display} />
  );
};

'use client';

import {
  AlertCircleIcon,
  ClipboardIcon,
  FileIcon,
  FileTextIcon,
  ImageUpIcon,
  Loader2,
  PlusIcon,
  Replace,
  XIcon,
} from 'lucide-react';

import { FileWithPreview, useFileUpload } from '@/hooks/use-file-upload';
import React, { useEffect } from 'react';
import Image from 'next/image';
import { deleteFile, getFile } from '@/lib/form-field/file-storage';
import { useMutation } from '@tanstack/react-query';
import { toast } from 'sonner';
import { Button } from '@/components/form-field/ui/button';
import { OneOrManyFile } from '@/lib/form-field/zod-schemas/file';
import { createSyntheticInputChange } from '@/lib/form-field/utils';

// Function to determine file type
export const getFileType = (fileType: string | null): 'image' | 'pdf' | 'other' => {
  if (!fileType) return 'other';
  if (fileType.startsWith('image/')) return 'image';
  if (fileType === 'application/pdf') return 'pdf';
  return 'other';
};

// Function to get appropriate icon for file type
const getFileIcon = (fileType: string) => {
  if (fileType.startsWith('image/')) return <ImageUpIcon className='size-6' />;
  if (fileType === 'application/pdf') return <FileTextIcon className='size-6' />;
  return <FileIcon className='size-6' />;
};

interface ImageDropZoneProps {
  display?: 'COMPACT' | 'FULL' | undefined;
}

export default function ImageDropZone(props: React.ComponentProps<'input'> & ImageDropZoneProps) {
  const maxSizeMB = 5;
  const maxSize = maxSizeMB * 1024 * 1024; // 5MB default
  const parsedValue = props.value as unknown as OneOrManyFile;
  const isCompact = props.display === 'COMPACT';

  const [
    { files, isDragging, errors },
    {
      handleDragEnter,
      handleDragLeave,
      handleDragOver,
      handleDrop,
      openFileDialog,
      removeFile,
      addFiles,
      getInputProps,
    },
  ] = useFileUpload({
    accept: props.accept || 'image/*,application/pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx,.txt',
    multiple: props.multiple || false,
    maxSize,
    onFilesChange: (files) => {
      // Handle file change event
      if (props.onChange) {
        if (props.multiple) {
          props.onChange(createSyntheticInputChange(props.name || '', files));
          return;
        }
        props.onChange(createSyntheticInputChange(props.name || '', files.at(0)));
      }
    },
  });

  useEffect(() => {
    if (!parsedValue) return;

    const loadFiles = async () => {
      if (Array.isArray(parsedValue)) {
        const loadedFiles = await Promise.all(
          parsedValue.filter((v) => v?.id).map((v) => getFile(v.id as string)),
        );
        addFiles(loadedFiles.filter(Boolean));
      } else if (parsedValue?.id) {
        const file = await getFile(parsedValue.id);
        if (file) addFiles([file]);
      }
    };

    loadFiles();
    return () => {};
  }, []);

  const deleteMutation = useMutation({
    mutationFn: async (fileId: string) => {
      // Delete the file from storage
      await deleteFile(fileId);
      // Remove the file from the state
      removeFile(fileId);
    },
    onError: (error) => {
      toast.error(error.message);
    },
  });

  // Render file preview based on type
  const renderFilePreview = (file: FileWithPreview, index: number) => {
    const fileType = getFileType(file.file.type);

    return (
      <div
        key={file.id || index}
        className={`relative h-full w-full overflow-hidden rounded-md ${files.length === 1 ? 'min-h-[200px]' : 'min-h-[100px]'} bg-background border`}
      >
        {fileType === 'image' && (
          <Image
            src={file.preview || ''}
            alt={file.file?.name || 'Uploaded image'}
            fill
            className='object-contain'
          />
        )}

        {fileType === 'pdf' && (
          <div className='flex h-full flex-col items-center justify-center p-2'>
            <FileTextIcon
              className={`${files.length === 1 ? 'size-12' : 'size-8'} mb-1 text-red-500`}
            />
            <p
              className={`${files.length === 1 ? 'text-sm' : 'text-xs'} max-w-full truncate font-medium`}
            >
              {file.file?.name}
            </p>
            <a
              href={file.preview}
              target='_blank'
              rel='noopener noreferrer'
              className={`${files.length === 1 ? 'text-sm' : 'text-xs'} mt-1 text-blue-500`}
            >
              View PDF
            </a>
          </div>
        )}

        {fileType === 'other' && (
          <div className='flex h-full flex-col items-center justify-center p-2'>
            {getFileIcon(file.file?.type || '')}
            <p
              className={`${files.length === 1 ? 'text-sm' : 'text-xs'} mt-1 max-w-full truncate font-medium`}
            >
              {file.file?.name}
            </p>
          </div>
        )}

        <div className='absolute top-2 right-2'>
          <button
            type='button'
            className='focus-visible:ring-ring/50 z-10 flex size-7 cursor-pointer items-center justify-center rounded-full bg-black/60 text-white transition-[color,box-shadow] outline-none hover:bg-black/80 focus-visible:ring-[2px]'
            onClick={() => {
              if (file.id) {
                deleteMutation.mutate(file.id);
              } else {
                removeFile(index.toString());
              }
            }}
            aria-label='Remove file'
          >
            {deleteMutation.isPending && deleteMutation.variables === file.id ? (
              <Loader2 className='size-4 animate-spin' />
            ) : (
              <XIcon className='size-4' aria-hidden='true' />
            )}
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className='flex flex-col gap-2'>
      <div className='relative'>
        {isCompact ? (
          <div
            role='button'
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            data-dragging={isDragging || undefined}
            className='border-input hover:bg-accent/50 data-[dragging=true]:bg-accent/50 has-[input:focus]:border-ring has-[input:focus]:ring-ring/50 relative flex h-10 min-h-10 w-full items-center overflow-hidden rounded-md border border-dashed px-2 transition-colors has-disabled:pointer-events-none has-disabled:opacity-50 has-[input:focus]:ring-[3px]'
          >
            <input
              {...getInputProps()}
              ref={(ref) => {
                if (ref) {
                  // @ts-expect-error type error
                  getInputProps().ref.current = ref;
                  // @ts-expect-error type error
                  props.ref.current = ref;
                }
              }}
              className='sr-only'
              aria-label='Upload file'
            />
            {files.length > 0 ? (
              <div className='flex w-full items-center gap-2 overflow-x-auto'>
                {files.map((file, index) => {
                  const fileType = getFileType(file.file.type);
                  return (
                    <div
                      key={file.id || index}
                      className='bg-background inline-flex items-center gap-2 rounded-md border px-2 py-1 text-xs'
                    >
                      {fileType === 'image' ? (
                        <div className='relative size-5 overflow-hidden rounded'>
                          <Image
                            src={file.preview || ''}
                            alt={file.file?.name || 'Preview'}
                            fill
                            className='object-cover'
                          />
                        </div>
                      ) : fileType === 'pdf' ? (
                        <FileTextIcon className='size-4 text-red-500' />
                      ) : (
                        <FileIcon className='size-4' />
                      )}
                      <span className='max-w-[10rem] truncate'>{file.file?.name}</span>
                      <button
                        type='button'
                        className='focus-visible:ring-ring/50 text-muted-foreground hover:bg-muted hover:text-foreground z-10 flex size-5 items-center justify-center rounded-full transition-[color,box-shadow] outline-none focus-visible:ring-[2px]'
                        onClick={() => {
                          if (file.id) {
                            deleteMutation.mutate(file.id);
                          } else {
                            removeFile(index.toString());
                          }
                        }}
                        aria-label='Remove file'
                      >
                        {deleteMutation.isPending && deleteMutation.variables === file.id ? (
                          <Loader2 className='size-3.5 animate-spin' />
                        ) : (
                          <XIcon className='size-3.5' aria-hidden='true' />
                        )}
                      </button>
                    </div>
                  );
                })}
                <Button
                  className={'text-xs'}
                  variant={'secondary'}
                  icon={
                    props.multiple ? (
                      <PlusIcon className='size-4' />
                    ) : (
                      <Replace className='size-4' />
                    )
                  }
                  size={'xs2'}
                  type={'button'}
                  onClick={openFileDialog}
                >
                  {props.multiple ? 'Add Files' : 'Replace File'}
                </Button>
              </div>
            ) : (
              <div
                className='flex w-full cursor-pointer items-center gap-2'
                onClick={openFileDialog}
              >
                <div
                  className='bg-background flex size-7 shrink-0 items-center justify-center rounded border'
                  aria-hidden='true'
                >
                  <ImageUpIcon className='size-4 opacity-60' />
                </div>
                <p className='text-muted-foreground truncate text-sm'>
                  {props.placeholder || (
                    <>Drop your {props.multiple ? 'files' : 'file'} here or click to browse</>
                  )}
                </p>
              </div>
            )}
          </div>
        ) : (
          <div
            role='button'
            onDragEnter={handleDragEnter}
            onDragLeave={handleDragLeave}
            onDragOver={handleDragOver}
            onDrop={handleDrop}
            data-dragging={isDragging || undefined}
            className='border-input hover:bg-accent/50 data-[dragging=true]:bg-accent/50 has-[input:focus]:border-ring has-[input:focus]:ring-ring/50 relative flex min-h-52 flex-col items-center justify-center overflow-hidden rounded-xl border border-dashed p-4 transition-colors has-disabled:pointer-events-none has-disabled:opacity-50 has-[input:focus]:ring-[3px]'
          >
            <input
              {...getInputProps()}
              ref={(ref) => {
                if (ref) {
                  // @ts-expect-error type error
                  getInputProps().ref.current = ref;
                  // @ts-expect-error type error
                  props.ref.current = ref;
                }
              }}
              className='sr-only'
              aria-label='Upload file'
            />

            {files.length > 0 ? (
              files.length === 1 ? (
                <div className='flex h-full w-full items-center justify-center'>
                  {renderFilePreview(files[0], 0)}
                </div>
              ) : (
                <div className='grid h-full w-full grid-cols-2 gap-2 sm:grid-cols-3'>
                  {files.map((file, index) => renderFilePreview(file, index))}
                </div>
              )
            ) : (
              <div
                className='flex cursor-pointer flex-col items-center justify-center px-4 py-3 text-center'
                onClick={openFileDialog}
              >
                <div
                  className='bg-background mb-2 flex size-11 shrink-0 items-center justify-center rounded-full border'
                  aria-hidden='true'
                >
                  <ImageUpIcon className='size-4 opacity-60' />
                </div>
                <p className='mb-1.5 text-sm font-medium'>
                  {props.placeholder || (
                    <>Drop your {props.multiple ? 'files' : 'file'} here or click to browse</>
                  )}
                </p>
                <p className='text-muted-foreground text-xs'>
                  Max size: {maxSizeMB}MB {props.multiple ? 'per file' : ''}
                </p>
              </div>
            )}
          </div>
        )}
      </div>
      {!isCompact && (props.multiple || files.length === 0) ? (
        <div className='mb-2 flex justify-between'>
          <Button
            type={'button'}
            onClick={async () => {
              let file: File | null = null;
              const items = await navigator.clipboard.read();
              for (const item of items) {
                for (const type of item.types) {
                  if (type.startsWith('image/')) {
                    const blob = await item.getType(type);
                    file = new File([blob], `clipboard-image-${Date.now()}-.png`, {
                      type: blob.type,
                    });
                  }
                }
              }

              if (!file) return toast.error('No image found in clipboard');
              addFiles([file]);
            }}
            size={'sm'}
            variant={'link'}
            className={'text-xs'}
            icon={<ClipboardIcon />}
          >
            Get from Clipboard
          </Button>
          <button
            type='button'
            onClick={openFileDialog}
            className='bg-primary text-primary-foreground hover:bg-primary/90 flex items-center gap-1 rounded-md px-3 py-1.5 text-sm'
          >
            <PlusIcon className='size-4' />
            Add {props.multiple ? 'Files' : 'File'}
          </button>
        </div>
      ) : null}

      {errors.length > 0 && (
        <div className='text-destructive flex items-center gap-1 text-xs' role='alert'>
          <AlertCircleIcon className='size-3 shrink-0' />
          <span>{errors[0]}</span>
        </div>
      )}
    </div>
  );
}

import React from 'react';
import { Input } from '@/components/ui/input';
import { BaseFieldProps } from '@/lib/form-field/form-field';

export const NumberField = (field: BaseFieldProps) => {
  return <Input {...field} type='number' />;
};

import React from 'react';
import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectLabel,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { cn, createSyntheticInputChange } from '@/lib/form-field/utils';
import { useOptionQuery } from '@/hooks/useOptionQuery';
import { BaseFieldProps, OptionType } from '@/lib/form-field/form-field';

export const SelectField: React.FC<
  BaseFieldProps & {
    options: OptionType;
    id?: string;
    customOnChange?: (value: string) => void;
    getOptionDisabled?: (value: string) => boolean;
    classNames?: {
      trigger?: string;
    };
  }
> = ({ name, id = name, customOnChange, classNames, ...field }) => {
  const options = useOptionQuery(field.options, id);
  return (
    <Select
      {...field}
      onValueChange={(e) => {
        field.onChange(createSyntheticInputChange(name, e));
        customOnChange?.(e);
      }}
    >
      <SelectTrigger className={cn('bg-secondary w-full', classNames?.trigger)}>
        <SelectValue placeholder={field.placeholder} />
      </SelectTrigger>
      <SelectContent>
        <SelectGroup>
          <SelectLabel>{field.label}</SelectLabel>
          {options.isLoading ? (
            <SelectItem value='loading' disabled>
              Loading...
            </SelectItem>
          ) : options.isError ? (
            <SelectItem value='error' disabled>
              Error loading options
            </SelectItem>
          ) : (
            options.data?.map(({ value, label }) => (
              <SelectItem
                key={value}
                value={value}
                disabled={field.getOptionDisabled?.(value) || false}
              >
                {label}
              </SelectItem>
            ))
          )}
        </SelectGroup>
      </SelectContent>
    </Select>
  );
};

import { Minus, Plus } from 'lucide-react';

import { Button, buttonVariants } from '@/components/form-field/ui/button';
import React, { JSX, useEffect, useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import { cn } from '@/lib/data-table/utils';

export default function Stepper({
  onIncrement,
  onDecrement,
  defaultValue = 0,
  min = Number.NEGATIVE_INFINITY,
  max = Number.POSITIVE_INFINITY,
  defaultDecrementIcon = <Minus size={16} aria-hidden='true' />,
  defaultIncrementIcon = <Plus size={16} aria-hidden='true' />,
  className,
  buttonVariant,
}: {
  onIncrement: (number: number) => Promise<void>;
  onDecrement: (number: number) => Promise<void>;
  defaultValue?: number;
  max?: number;
  min?: number;
  defaultDecrementIcon?: JSX.Element;
  defaultIncrementIcon?: JSX.Element;
  className?: string;
  buttonVariant?: typeof buttonVariants;
}) {
  const [value, setValue] = useState<number>(defaultValue);
  useEffect(() => {
    setValue(defaultValue);
  }, [defaultValue]);
  const onIncrementMutation = useMutation({
    mutationFn: () => {
      const newValue = Math.min(max, value + 1);
      setValue(newValue);
      return onIncrement(newValue);
    },
  });
  const onDecrementMutation = useMutation({
    mutationFn: () => {
      const newValue = Math.max(min, value - 1);
      setValue(newValue);
      return onDecrement(newValue);
    },
  });

  return (
    <div className='inline-flex -space-x-px rounded-full shadow-xs rtl:space-x-reverse'>
      <Button
        isLoading={onDecrementMutation.isPending}
        onClick={() => onDecrementMutation.mutate()}
        disabled={value <= min}
        {...buttonVariant}
        className={cn(
          'rounded-none shadow-none first:rounded-s-full last:rounded-e-full focus-visible:z-10',
          className,
        )}
        size='icon'
        aria-label='Upvote'
        icon={defaultDecrementIcon}
      />
      <span className='bg-primary text-primary-foreground flex items-center px-1 text-sm font-medium'>
        {value}
      </span>
      <Button
        isLoading={onIncrementMutation.isPending}
        onClick={() => onIncrementMutation.mutate()}
        disabled={value >= max}
        {...buttonVariant}
        className={cn(
          'rounded-none shadow-none first:rounded-s-full last:rounded-e-full focus-visible:z-10',
          className,
        )}
        size='icon'
        aria-label='Downvote'
        icon={defaultIncrementIcon}
      />
    </div>
  );
}

