  itemClassName,
  options,
  ...props
}: BaseFieldProps & {
  options: OptionType;
  className?: string;
  itemClassName?: string;
}) {
  const optionsQuery = useOptionQuery(options, props.name);
  if (optionsQuery.isPending) {
