import type React from "react";
import { Checkbox } from "@/components/ui/checkbox";
import type { BaseFieldProps } from "@/lib/form-field/form-field";

export const BooleanField: React.FC<BaseFieldProps> = (field) => {
	return (
		<Checkbox
			onCheckedChange={field.onChange}
			checked={field.value}
			{...field}
		/>
	);
};

import { CommandLoading } from "cmdk";
import { debounce } from "lodash-es";
import { Check, ChevronsUpDown, Loader2 } from "lucide-react";
import type React from "react";
import { useCallback, useEffect, useMemo, useRef, useState } from "react";
import { Button } from "@/components/ui/button";
import {
	Command,
	CommandEmpty,
	CommandGroup,
	CommandInput,
	CommandItem,
	CommandList,
} from "@/components/ui/command";
import { FormControl } from "@/components/ui/form";
import {
	Popover,
	PopoverContent,
	PopoverTrigger,
} from "@/components/ui/popover";
import { useOptionQuery } from "@/hooks/useOptionQuery";
import type {
	BaseFieldProps,
	baseOption,
	Options,
} from "@/lib/form-field/form-field";
import { cn, createSyntheticInputChange } from "@/lib/form-field/utils";

export const ComboboxField: React.FC<
	BaseFieldProps & {
		options: (searchQuery: string | undefined) => Options;
		noResultFallBack?: React.ReactNode;
		getInitialValue?: (value: string) => Promise<baseOption | undefined>;
	}
> = ({
	noResultFallBack = "No Result found.",
	options: optionFn,
	getInitialValue,
	...field
}) => {
	const [searchQuery, setSearchQuery] = useState<string>();
	const [customOptions, setCustomOptions] = useState<baseOption[]>([]);
	const [open, setOpen] = useState(false);
	const initialLoadRef = useRef(false);

	const optionsQuery = useOptionQuery(() => optionFn(searchQuery), field.name, {
		q: searchQuery,
	});

	// Memoize debounced function to prevent recreation
	const debouncedSetSearchQuery = useMemo(
		() => debounce((value: string) => setSearchQuery(value), 500),
		[],
	);

	// Cleanup debounce on unmount
	useEffect(() => {
		return () => {
			debouncedSetSearchQuery.cancel();
		};
	}, [debouncedSetSearchQuery]);

	// Load initial value only once
	useEffect(() => {
		if (!field.value || !getInitialValue || initialLoadRef.current) return;

		initialLoadRef.current = true;

		getInitialValue(field.value).then((opt) => {
			if (opt?.value) {
				field.onChange(createSyntheticInputChange(field.name, opt.value));
				setCustomOptions((prev) => {
					// Avoid duplicates
					const exists = prev.some((o) => o.value === opt.value);
					return exists ? prev : [opt, ...prev];
				});
			} else {
				field.onChange(null);
			}
		});
	}, [field.value, getInitialValue, field.name]);

	// Memoize combined options
	const options = useMemo(
		() => customOptions.concat(optionsQuery.data || []),
		[customOptions, optionsQuery.data],
	);

	// Memoize selected value
	const selectedOption = useMemo(
		() =>
			field.value
				? options.find((opt) => opt.value === field.value)
				: undefined,
		[field.value, options],
	);

	// Memoize handlers
	const handleSelect = useCallback(
		(value: string) => {
			setOpen(false);
			field.onChange(createSyntheticInputChange(field.name, value));
		},
		[field.name, field.onChange],
	);

	const handleOpenChange = useCallback((isOpen: boolean) => {
		setOpen(isOpen);
	}, []);

	return (
		<div className="relative">
			<Popover open={open} onOpenChange={handleOpenChange}>
				<PopoverTrigger asChild>
					<FormControl className="w-full">
						<Button
							variant="outline"
							role="combobox"
							aria-expanded={open}
							className={cn(
								"justify-between",
								field.className,
								!field.value && "text-muted-foreground",
							)}
						>
							{selectedOption?.label || (
								<span className="text-muted-foreground max-w-32 truncate">
									Select {field.label}
								</span>
							)}
							<ChevronsUpDown className="opacity-50" />
						</Button>
					</FormControl>
				</PopoverTrigger>
				<PopoverContent
					className="w-[var(--radix-popover-trigger-width)] p-0"
					align="center"
				>
					<Command>
						<CommandInput
							placeholder={field.placeholder}
							className="h-9"
							onValueChange={debouncedSetSearchQuery}
						/>
						<CommandList>
							{optionsQuery.isPending && (
								<CommandLoading>
									<div className="flex items-center justify-center py-4">
										<Loader2 className="animate-spin" />
									</div>
								</CommandLoading>
							)}
							{!optionsQuery.isPending && options.length === 0 && (
								<CommandEmpty>{noResultFallBack}</CommandEmpty>
							)}

							{options.length > 0 && (
								<CommandGroup>
									{options.map(({ label, value, icon: Icon, wrapperFn }) => (
										<CommandItem
											value={label}
											key={value}
											className="*:w-full"
											onSelect={() => handleSelect(value)}
										>
											{wrapperFn ? (
												wrapperFn({ label, icon: Icon, value })
											) : (
												<div className="flex w-full items-center justify-between gap-2">
													<span className="flex items-center gap-2">
														{Icon && <Icon />} {label}
													</span>
												</div>
											)}
											<Check
												className={cn(
													"ml-auto",
													value === field.value ? "opacity-100" : "opacity-0",
												)}
											/>
										</CommandItem>
									))}
								</CommandGroup>
							)}
						</CommandList>
					</Command>
				</PopoverContent>
			</Popover>
		</div>
	);
};

import type React from "react";
import { useState } from "react";
import { Input } from "@/components/ui/input";
import type { BaseFieldProps } from "@/lib/form-field/form-field";
import { createSyntheticInputChange } from "@/lib/form-field/utils";

const moneyFormatter = Intl.NumberFormat("en-IN", {
	currency: "INR",
	style: "currency",
	minimumFractionDigits: 2,
	maximumFractionDigits: 2,
});

export const CurrencyField: React.FC<BaseFieldProps> = ({ ...field }) => {
	const [displayValue, setDisplayValue] = useState<string>(
		field.value ? moneyFormatter.format(Number(field.value)) : "",
	);

	function parseToNumber(input: string): number | null {
		// allow decimals directly, don't force paise
		const clean = input.replace(/[^0-9.-]/g, "");
		if (!clean) return null;
		return Number(clean);
	}

	function handleChange(e: React.ChangeEvent<HTMLInputElement>) {
		const raw = e.target.value;
		setDisplayValue(raw); // show what user typed

		const parsed = parseToNumber(raw);
		field.onChange(createSyntheticInputChange(field.name, parsed ?? "")); // keep form state updated live
	}

	function handleBlur() {
		field.onBlur();
		const parsed = parseToNumber(displayValue);
		if (parsed !== null && !Number.isNaN(parsed)) {
			setDisplayValue(moneyFormatter.format(parsed));
		} else {
			setDisplayValue("");
		}
	}

	function handleFocus() {
		// strip formatting so user edits raw value
		const parsed = parseToNumber(displayValue);
		if (parsed !== null && !Number.isNaN(parsed)) {
			setDisplayValue(String(parsed));
		}
	}

	return (
		<Input
			type="text"
			inputMode="decimal"
			{...field}
			value={displayValue}
			onChange={handleChange}
			onBlur={handleBlur}
			onFocus={handleFocus}
		/>
	);
};

"use client";

import { PopoverClose } from "@radix-ui/react-popover";
import { format } from "date-fns";
import { ChevronDownIcon, ClockIcon } from "lucide-react";
import type React from "react";
import { useEffect, useRef, useState } from "react";
import { Button } from "@/components/form-field/ui/button";
import { Calendar } from "@/components/ui/calendar";
import { Input } from "@/components/ui/input";
import {
	Popover,
	PopoverContent,
	PopoverTrigger,
} from "@/components/ui/popover";
import type { BaseFieldProps } from "@/lib/form-field/form-field";
import { createSyntheticInputChange } from "@/lib/form-field/utils";
import { cn } from "@/lib/utils";

const parseDate = (val: unknown): Date | undefined => {
	if (!val && val !== 0) return undefined;
	if (val instanceof Date) return val;
	const d = new Date(String(val));
	return isNaN(d.getTime()) ? undefined : d;
};

export const DateTimeField: React.FC<
	BaseFieldProps & {
		display?: "date" | "time" | "datetime";
	}
> = ({ display = "datetime", ...field }) => {
	// helper to parse/normalize incoming prop value

	const [date, setDate] = useState<Date | undefined>(() =>
		parseDate(field.value),
	);
	const popoverCloseRef = useRef<HTMLButtonElement | null>(null);

	// keep internal state in sync when parent updates value
	useEffect(() => {
		setDate(parseDate(field.value));
	}, [field.value]);

	const shouldDisplayTime = display === "time" || display === "datetime";
	const shouldDisplayDate = display === "date" || display === "datetime";
	return (
		<div className="flex w-full gap-1">
			{shouldDisplayDate && (
				<div className="flex flex-1 flex-col gap-3">
					<Popover>
						<PopoverTrigger asChild>
							<Button
								variant="ghost"
								id="date-picker"
								className={cn(
									"w-full justify-between border border-input bg-secondary font-normal selection:bg-primary selection:text-primary-foreground file:text-foreground placeholder:text-muted-foreground dark:bg-input/30",
									field.className,
									{
										// use internal date state to determine muted styling
										"text-muted-foreground": !date,
									},
								)}
							>
								{date
									? format(date, "PPP")
									: field.placeholder || "Select date"}
								<ChevronDownIcon />
							</Button>
						</PopoverTrigger>
						<PopoverClose ref={popoverCloseRef} hidden={true} />
						<PopoverContent
							className="w-auto overflow-hidden p-0"
							align="start"
						>
							<Calendar
								mode="single"
								selected={date}
								captionLayout="dropdown"
								onSelect={(selected) => {
									if (!selected) return;
									setDate(selected);
									popoverCloseRef.current?.click();
									field.onChange(
										createSyntheticInputChange(
											field.name,
											selected.toISOString(),
										),
									);
								}}
							/>
						</PopoverContent>
					</Popover>
				</div>
			)}
			{shouldDisplayTime && (
				<div
					className={cn(
						"flex flex-1 flex-col gap-3 rounded-md",
						field.className,
						{
							// use internal date state to determine muted styling
							"text-muted-foreground": !date,
						},
					)}
				>
					<div className="relative grow">
						<Input
							id={field.name}
							type="time"
							step="1"
							// make the time input controlled so it reflects external updates
							value={date ? format(date, "HH:mm:ss") : ""}
							onChange={(e) => {
								const timeValue = e.target.value;
								if (!timeValue) {
									// clear time -> clear date state / notify parent with empty value
									setDate(undefined);
									field.onChange(createSyntheticInputChange(field.name, ""));
									return;
								}
								const parts = timeValue.split(":").map(Number);
								const [hours = 0, minutes = 0, seconds = 0] = parts;
								const newDate = new Date(date || Date.now());
								newDate.setHours(hours, minutes, seconds);
								setDate(newDate);
								field.onChange(
									createSyntheticInputChange(field.name, newDate.toISOString()),
								);
							}}
							className="peer appearance-none ps-9 [&::-webkit-calendar-picker-indicator]:hidden [&::-webkit-calendar-picker-indicator]:appearance-none"
						/>
						<div className="pointer-events-none absolute inset-y-0 start-0 flex items-center justify-center ps-3 text-muted-foreground/80 peer-disabled:opacity-50">
							<ClockIcon size={16} aria-hidden="true" />
						</div>
					</div>
				</div>
			)}
		</div>
	);
};

import ImageDropZone from "@/components/form-field/ui/ImageDropZone";
import type { BaseFieldProps } from "@/lib/form-field/form-field";

export const FileField = ({
	display = "COMPACT",
	...field
}: BaseFieldProps & {
	multiple?: boolean;
	accept?: string;
	display?: "COMPACT" | "FULL";
}) => {
	return (
		<ImageDropZone
			{...field}
			accept={field.accept}
			multiple={field.multiple}
			display={display}
		/>
	);
};

"use client";

import { useMutation } from "@tanstack/react-query";
import {
	AlertCircleIcon,
	ClipboardIcon,
	FileIcon,
	FileTextIcon,
	ImageUpIcon,
	Loader2,
	PlusIcon,
	Replace,
	XIcon,
} from "lucide-react";
import Image from "next/image";
import type React from "react";
import { useEffect } from "react";
import { toast } from "sonner";
import { Button } from "@/components/form-field/ui/button";
import { type FileWithPreview, useFileUpload } from "@/hooks/use-file-upload";
import { deleteFile, getFile } from "@/lib/form-field/file-storage";
import { cn, createSyntheticInputChange } from "@/lib/form-field/utils";
import type { OneOrManyFile } from "@/lib/form-field/zod-schemas/file";

// Function to determine file type
export const getFileType = (
	fileType: string | null,
): "image" | "pdf" | "other" => {
	if (!fileType) return "other";
	if (fileType.startsWith("image/")) return "image";
	if (fileType === "application/pdf") return "pdf";
	return "other";
};

// Function to get appropriate icon for file type
const getFileIcon = (fileType: string) => {
	if (fileType.startsWith("image/")) return <ImageUpIcon className="size-6" />;
	if (fileType === "application/pdf")
		return <FileTextIcon className="size-6" />;
	return <FileIcon className="size-6" />;
};

interface ImageDropZoneProps {
	display?: "COMPACT" | "FULL" | undefined;
}

export default function ImageDropZone({
	className,
	...props
}: React.ComponentProps<"input"> & ImageDropZoneProps) {
	const maxSizeMB = 5;
	const maxSize = maxSizeMB * 1024 * 1024; // 5MB default
	const parsedValue = props.value as unknown as OneOrManyFile;
	const isCompact = props.display === "COMPACT";

	const [
		{ files, isDragging, errors },
		{
			handleDragEnter,
			handleDragLeave,
			handleDragOver,
			handleDrop,
			openFileDialog,
			removeFile,
			addFiles,
			getInputProps,
		},
	] = useFileUpload({
		accept:
			props.accept ||
			"image/*,application/pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx,.txt",
		multiple: props.multiple || false,
		maxSize,
		onFilesChange: (files) => {
			// Handle file change event
			if (props.onChange) {
				if (props.multiple) {
					props.onChange(createSyntheticInputChange(props.name || "", files));
					return;
				}
				props.onChange(
					createSyntheticInputChange(props.name || "", files.at(0)),
				);
			}
		},
	});

	useEffect(() => {
		if (!parsedValue) return;

		const loadFiles = async () => {
			if (Array.isArray(parsedValue)) {
				const loadedFiles = await Promise.all(
					parsedValue.filter((v) => v?.id).map((v) => getFile(v.id as string)),
				);
				addFiles(loadedFiles.filter(Boolean));
			} else if (parsedValue?.id) {
				const file = await getFile(parsedValue.id);
				if (file) addFiles([file]);
			}
		};

		loadFiles();
		return () => {};
	}, [addFiles, parsedValue]);

	const deleteMutation = useMutation({
		mutationFn: async (fileId: string) => {
			// Delete the file from storage
			await deleteFile(fileId);
			// Remove the file from the state
			removeFile(fileId);
		},
		onError: (error) => {
			toast.error(error.message);
		},
	});

	// Render file preview based on type
	const renderFilePreview = (file: FileWithPreview, index: number) => {
		const fileType = getFileType(file.file.type);

		return (
			<div
				key={file.id || index}
				className={`relative h-full w-full overflow-hidden rounded-md ${files.length === 1 ? "min-h-[200px]" : "min-h-[100px]"} border bg-background`}
			>
				{fileType === "image" && (
					<Image
						src={file.preview || ""}
						alt={file.file?.name || "Uploaded image"}
						fill
						className="object-contain"
					/>
				)}

				{fileType === "pdf" && (
					<div className="flex h-full flex-col items-center justify-center p-2">
						<FileTextIcon
							className={`${files.length === 1 ? "size-12" : "size-8"} mb-1 text-red-500`}
						/>
						<p
							className={`${files.length === 1 ? "text-sm" : "text-xs"} max-w-full truncate font-medium`}
						>
							{file.file?.name}
						</p>
						<a
							href={file.preview}
							target="_blank"
							rel="noopener noreferrer"
							className={`${files.length === 1 ? "text-sm" : "text-xs"} mt-1 text-blue-500`}
						>
							View PDF
						</a>
					</div>
				)}

				{fileType === "other" && (
					<div className="flex h-full flex-col items-center justify-center p-2">
						{getFileIcon(file.file?.type || "")}
						<p
							className={`${files.length === 1 ? "text-sm" : "text-xs"} mt-1 max-w-full truncate font-medium`}
						>
							{file.file?.name}
						</p>
					</div>
				)}

				<div className="absolute top-2 right-2">
					<button
						type="button"
						className="z-10 flex size-7 cursor-pointer items-center justify-center rounded-full bg-black/60 text-white outline-none transition-[color,box-shadow] hover:bg-black/80 focus-visible:ring-[2px] focus-visible:ring-ring/50"
						onClick={() => {
							if (file.id) {
								deleteMutation.mutate(file.id);
							} else {
								removeFile(index.toString());
							}
						}}
						aria-label="Remove file"
					>
						{deleteMutation.isPending &&
						deleteMutation.variables === file.id ? (
							<Loader2 className="size-4 animate-spin" />
						) : (
							<XIcon className="size-4" aria-hidden="true" />
						)}
					</button>
				</div>
			</div>
		);
	};

	return (
		<div className={cn("flex flex-col gap-2 rounded-md", className)}>
			<div className="relative">
				{isCompact ? (
					<div
						tabIndex={0}
						role="button"
						onDragEnter={handleDragEnter}
						onDragLeave={handleDragLeave}
						onDragOver={handleDragOver}
						onDrop={handleDrop}
						data-dragging={isDragging || undefined}
						className="relative flex h-10 min-h-10 w-full items-center overflow-hidden rounded-md border border-input border-dashed px-2 transition-colors hover:bg-accent/50 has-disabled:pointer-events-none has-[input:focus]:border-ring has-disabled:opacity-50 has-[input:focus]:ring-[3px] has-[input:focus]:ring-ring/50 data-[dragging=true]:bg-accent/50"
					>
						<input
							{...getInputProps()}
							ref={(ref) => {
								if (ref) {
									// @ts-expect-error type error
									getInputProps().ref.current = ref;
									// @ts-expect-error type error
									props.ref.current = ref;
								}
							}}
							className="sr-only"
							aria-label="Upload file"
						/>
						{files.length > 0 ? (
							<div className="flex w-full items-center gap-2 overflow-x-auto">
								{files.map((file, index) => {
									const fileType = getFileType(file.file.type);
									return (
										<div
											key={file.id || index}
											className="inline-flex items-center gap-2 rounded-md border bg-background px-2 py-1 text-xs"
										>
											{fileType === "image" ? (
												<div className="relative size-5 overflow-hidden rounded">
													<Image
														src={file.preview || ""}
														alt={file.file?.name || "Preview"}
														fill
														className="object-cover"
													/>
												</div>
											) : fileType === "pdf" ? (
												<FileTextIcon className="size-4 text-red-500" />
											) : (
												<FileIcon className="size-4" />
											)}
											<span className="max-w-[10rem] truncate">
												{file.file?.name}
											</span>
											<button
												type="button"
												className="z-10 flex size-5 items-center justify-center rounded-full text-muted-foreground outline-none transition-[color,box-shadow] hover:bg-muted hover:text-foreground focus-visible:ring-[2px] focus-visible:ring-ring/50"
												onClick={() => {
													if (file.id) {
														deleteMutation.mutate(file.id);
													} else {
														removeFile(index.toString());
													}
												}}
												aria-label="Remove file"
											>
												{deleteMutation.isPending &&
												deleteMutation.variables === file.id ? (
													<Loader2 className="size-3.5 animate-spin" />
												) : (
													<XIcon className="size-3.5" aria-hidden="true" />
												)}
											</button>
										</div>
									);
								})}
								<Button
									className={"text-xs"}
									variant={"secondary"}
									icon={
										props.multiple ? (
											<PlusIcon className="size-4" />
										) : (
											<Replace className="size-4" />
										)
									}
									size={"xs2"}
									type={"button"}
									onClick={openFileDialog}
								>
									{props.multiple ? "Add Files" : "Replace File"}
								</Button>
							</div>
						) : (
							<button
								type={"button"}
								className="flex w-full cursor-pointer items-center gap-2"
								onClick={openFileDialog}
							>
								<div
									className="flex size-7 shrink-0 items-center justify-center rounded border bg-background"
									aria-hidden="true"
								>
									<ImageUpIcon className="size-4 opacity-60" />
								</div>
								<p className="truncate text-muted-foreground text-sm">
									{props.placeholder || (
										<>
											Drop your {props.multiple ? "files" : "file"} here or
											click to browse
										</>
									)}
								</p>
							</button>
						)}
					</div>
				) : (
					<div
						role="button"
						onDragEnter={handleDragEnter}
						onDragLeave={handleDragLeave}
						onDragOver={handleDragOver}
						onDrop={handleDrop}
						data-dragging={isDragging || undefined}
						className="relative flex min-h-52 flex-col items-center justify-center overflow-hidden rounded-xl border border-input border-dashed p-4 transition-colors hover:bg-accent/50 has-disabled:pointer-events-none has-[input:focus]:border-ring has-disabled:opacity-50 has-[input:focus]:ring-[3px] has-[input:focus]:ring-ring/50 data-[dragging=true]:bg-accent/50"
					>
						<input
							{...getInputProps()}
							ref={(ref) => {
								if (ref) {
									// @ts-expect-error type error
									getInputProps().ref.current = ref;
									// @ts-expect-error type error
									props.ref.current = ref;
								}
							}}
							className="sr-only"
							aria-label="Upload file"
						/>

						{files.length > 0 ? (
							files.length === 1 ? (
								<div className="flex h-full w-full items-center justify-center">
									{renderFilePreview(files[0], 0)}
								</div>
							) : (
								<div className="grid h-full w-full grid-cols-2 gap-2 sm:grid-cols-3">
									{files.map((file, index) => renderFilePreview(file, index))}
								</div>
							)
						) : (
							<button
								type={"button"}
								className="flex cursor-pointer flex-col items-center justify-center px-4 py-3 text-center"
								onClick={openFileDialog}
							>
								<div
									className="mb-2 flex size-11 shrink-0 items-center justify-center rounded-full border bg-background"
									aria-hidden="true"
								>
									<ImageUpIcon className="size-4 opacity-60" />
								</div>
								<p className="mb-1.5 font-medium text-sm">
									{props.placeholder || (
										<>
											Drop your {props.multiple ? "files" : "file"} here or
											click to browse
										</>
									)}
								</p>
								<p className="text-muted-foreground text-xs">
									Max size: {maxSizeMB}MB {props.multiple ? "per file" : ""}
								</p>
							</button>
						)}
					</div>
				)}
			</div>
			{!isCompact && (props.multiple || files.length === 0) ? (
				<div className={cn("mb-2 flex justify-between", className)}>
					<Button
						type={"button"}
						onClick={async () => {
							let file: File | null = null;
							const items = await navigator.clipboard.read();
							for (const item of items) {
								for (const type of item.types) {
									if (type.startsWith("image/")) {
										const blob = await item.getType(type);
										file = new File(
											[blob],
											`clipboard-image-${Date.now()}-.png`,
											{
												type: blob.type,
											},
										);
									}
								}
							}

							if (!file) return toast.error("No image found in clipboard");
							addFiles([file]);
						}}
						size={"sm"}
						variant={"link"}
						className={"text-xs"}
						icon={<ClipboardIcon />}
					>
						Get from Clipboard
					</Button>
					<button
						type="button"
						onClick={openFileDialog}
						className="flex items-center gap-1 rounded-md bg-primary px-3 py-1.5 text-primary-foreground text-sm hover:bg-primary/90"
					>
						<PlusIcon className="size-4" />
						Add {props.multiple ? "Files" : "File"}
					</button>
				</div>
			) : null}

			{errors.length > 0 && (
				<div
					className="flex items-center gap-1 text-destructive text-xs"
					role="alert"
				>
					<AlertCircleIcon className="size-3 shrink-0" />
					<span>{errors[0]}</span>
				</div>
			)}
		</div>
	);
}

